import requests

# client_id 为官网获取的AK， client_secret 为官网获取的SK
host = 'https://aip.baidubce.com/oauth/2.0/token?grant_type=client_credentials&client_id=qbgc0hS69xG8FM2VukTsBtpU&client_secret=lmWexUmv8skhkuMorGlmylbrRFEwDFWm'
content = requests.get(host)
headers = {'Content-Type': 'application/json; charset=UTF-8'}
if content:
    print(content.text)
    access_token = content.json()['access_token']
else:
    raise TypeError('token_error')

post_url = 'https://aip.baidubce.com/rest/2.0/ocr/v1/accurate?access_token=' + access_token
headers = {'Content-Type': 'application/x-www-form-urlencoded'}
data = {
    'image': ''}
res = requests.post(post_url, headers=headers, data=data)
print(res.text)
